package cn.andrewlu.community.utils;import android.text.TextUtils;public class PayResult {	private String resultStatus;	private String result;	private String memo;	public PayResult(String rawResult) {		if (TextUtils.isEmpty(rawResult))			return;		String[] resultParams = rawResult.split(";");		for (String resultParam : resultParams) {			if (resultParam.startsWith("resultStatus")) {				resultStatus = gatValue(resultParam, "resultStatus");			}			if (resultParam.startsWith("result")) {				result = gatValue(resultParam, "result");			}			if (resultParam.startsWith("memo")) {				memo = gatValue(resultParam, "memo");			}		}	}	@Override	public String toString() {		return "resultStatus={" + resultStatus + "};memo={" + memo				+ "};result={" + result + "}";	}	private String gatValue(String content, String key) {		String prefix = key + "={";		return content.substring(content.indexOf(prefix) + prefix.length(),				content.lastIndexOf("}"));	}	/**	 * @return the resultStatus	 */	public String getResultStatus() {		return resultStatus;	}	/**	 * @return the memo	 */	public String getMemo() {		return memo;	}	/**	 * @return the result	 */	public String getResult() {		return result;	}	public int getResultStatusCode() {		return Integer.valueOf(resultStatus);	}	public final static int STATUS_SUCCESS = 9000;	public final static int STATUS_CHECKING = 8000;	public final static int STATUS_FAILED = 4000;	public final static int STATUS_CANCEL = 6001;	public final static int STATUS_NETERROR = 6002;}